import "core-js/modules/es.array.push.js";
import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.filter.js";
import "core-js/modules/es.iterator.find.js";
import "core-js/modules/es.iterator.map.js";
import "core-js/modules/es.json.stringify.js";
import "core-js/modules/esnext.json.parse.js";
import { ref, computed, onMounted, nextTick, watch } from 'vue';
import { useStore } from 'vuex';
import { ElMessage, ElMessageBox } from 'element-plus';
import { Plus, Fold, Expand, ChatLineRound, MoreFilled, Setting, Document, Delete, ChatDotRound, Cpu, Paperclip, Search, Globe, Promotion, Loading, Edit } from '@element-plus/icons-vue';
import api from '@/api';
import { marked } from 'marked';
import DOMPurify from 'dompurify';
import hljs from 'highlight.js';

// 配置 marked

export default {
  __name: 'KimiChat',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    marked.setOptions({
      highlight: function (code, lang) {
        if (lang && hljs.getLanguage(lang)) {
          return hljs.highlight(code, {
            language: lang
          }).value;
        }
        return hljs.highlightAuto(code).value;
      },
      breaks: true,
      gfm: true
    });
    const store = useStore();

    // ==================== 状态 ====================
    const sidebarCollapsed = ref(false);
    const currentChatId = ref(null);
    const chatHistory = ref([]);
    const messages = ref([]);
    const inputMessage = ref('');
    const isTyping = ref(false);
    const currentModel = ref('glm-4-plus');
    const messagesContainer = ref(null);
    const useRag = ref(true);
    const useWebSearch = ref(false);
    const selectedPapers = ref([]);
    const uploadedFiles = ref([]); // 已上传文件列表
    const isUploading = ref(false); // 上传中状态

    // 论文选择
    const showPaperSelector = ref(false);
    const paperSearch = ref('');
    const papers = ref([]);
    const tempSelectedPapers = ref([]);
    const paperTable = ref(null);

    // 设置
    const showSettings = ref(false);
    const settings = ref({
      model: 'glm-4-plus',
      temperature: 0.7,
      maxTokens: 4000,
      useRag: true,
      autoReference: true
    });

    // 重命名
    const showRenameDialog = ref(false);
    const renameValue = ref('');
    const renameChatId = ref(null);

    // ==================== 计算属性 ====================
    const userAvatar = computed(() => store.getters.currentUser?.avatar || '');
    const userInitial = computed(() => {
      const name = store.getters.currentUser?.full_name || store.getters.currentUser?.username || 'U';
      return name.charAt(0).toUpperCase();
    });
    const currentChatTitle = computed(() => {
      const chat = chatHistory.value.find(c => c.chat_id === currentChatId.value);
      return chat?.preview || '新对话';
    });
    const canSend = computed(() => {
      return inputMessage.value.trim().length > 0 && !isTyping.value;
    });
    const inputRows = computed(() => {
      const lines = inputMessage.value.split('\n').length;
      return Math.min(Math.max(lines, 1), 5);
    });
    const filteredPapers = computed(() => {
      if (!paperSearch.value) return papers.value;
      const query = paperSearch.value.toLowerCase();
      return papers.value.filter(p => (p.title || '').toLowerCase().includes(query) || (p.abstract || '').toLowerCase().includes(query));
    });

    // ==================== 快捷功能卡片 ====================
    const quickCards = [{
      key: 'summary',
      label: '文献综述',
      icon: 'Document',
      prompt: '请基于我的论文库生成一份文献综述'
    }, {
      key: 'gaps',
      label: '研究空白',
      icon: 'Search',
      prompt: '请分析当前研究领域的主要空白'
    }, {
      key: 'code',
      label: '代码生成',
      icon: 'Cpu',
      prompt: '请帮我生成实现XX算法的代码'
    }, {
      key: 'trends',
      label: '趋势分析',
      icon: 'TrendCharts',
      prompt: '请分析该领域的研究趋势'
    }];
    const exampleQuestions = ['帮我总结这几篇论文的核心贡献', '这些论文中使用了哪些主要方法？', '这个领域的研究现状如何？', '有哪些潜在的研究方向值得探索？', '帮我比较论文A和论文B的方法差异', '请解释论文中的XX算法原理'];

    // ==================== 方法 ====================
    const formatTime = timestamp => {
      if (!timestamp) return '';
      const date = new Date(timestamp);
      return date.toLocaleTimeString('zh-CN', {
        hour: '2-digit',
        minute: '2-digit'
      });
    };
    const renderMessage = content => {
      if (!content) return '';
      const html = marked(content);
      return DOMPurify.sanitize(html);
    };
    const scrollToBottom = () => {
      nextTick(() => {
        if (messagesContainer.value) {
          messagesContainer.value.scrollTop = messagesContainer.value.scrollHeight;
        }
      });
    };

    // ==================== 聊天操作 ====================
    const loadChatHistory = async () => {
      try {
        // 这里可以调用后端 API 获取会话列表
        // 目前使用本地存储
        const saved = localStorage.getItem('kimi_chat_history');
        if (saved) {
          chatHistory.value = JSON.parse(saved);
        }
      } catch (e) {
        console.error('加载聊天历史失败:', e);
      }
    };
    const saveChatHistory = () => {
      try {
        localStorage.setItem('kimi_chat_history', JSON.stringify(chatHistory.value));
      } catch (e) {
        console.error('保存聊天历史失败:', e);
      }
    };
    const createNewChat = () => {
      const newChatId = `chat_${Date.now()}`;
      currentChatId.value = newChatId;
      messages.value = [];
      selectedPapers.value = [];

      // 添加到历史
      chatHistory.value.unshift({
        chat_id: newChatId,
        preview: '新对话',
        created_at: new Date().toISOString(),
        message_count: 0
      });
      saveChatHistory();
    };
    const switchChat = async chatId => {
      if (currentChatId.value === chatId) return;
      currentChatId.value = chatId;

      // 加载该会话的消息历史
      try {
        const response = await api.getChatHistory(chatId);
        if (response.success) {
          messages.value = response.data || [];
        }
      } catch (e) {
        console.error('加载消息历史失败:', e);
        messages.value = [];
      }
      scrollToBottom();
    };
    const renameChat = chatId => {
      renameChatId.value = chatId;
      renameValue.value = '';
      showRenameDialog.value = true;
    };
    const confirmRename = () => {
      if (!renameValue.value.trim()) {
        ElMessage.warning('请输入新名称');
        return;
      }
      const chat = chatHistory.value.find(c => c.chat_id === renameChatId.value);
      if (chat) {
        chat.preview = renameValue.value;
        saveChatHistory();
        ElMessage.success('重命名成功');
      }
      showRenameDialog.value = false;
    };
    const deleteChat = async chatId => {
      try {
        await ElMessageBox.confirm('确定要删除这个对话吗？', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        });

        // 调用后端 API 删除
        await api.deleteChat({
          chatId
        });

        // 从列表中移除
        chatHistory.value = chatHistory.value.filter(c => c.chat_id !== chatId);

        // 如果删除的是当前会话，切换到第一个或新建
        if (currentChatId.value === chatId) {
          if (chatHistory.value.length > 0) {
            switchChat(chatHistory.value[0].chat_id);
          } else {
            createNewChat();
          }
        }
        saveChatHistory();
        ElMessage.success('删除成功');
      } catch (e) {
        if (e !== 'cancel') {
          console.error('删除失败:', e);
        }
      }
    };
    const clearCurrentChat = async () => {
      if (messages.value.length === 0) return;
      try {
        await ElMessageBox.confirm('确定要清空当前对话吗？', '提示', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        });
        await api.clearChat({
          chatId: currentChatId.value
        });
        messages.value = [];

        // 更新预览
        const chat = chatHistory.value.find(c => c.chat_id === currentChatId.value);
        if (chat) {
          chat.preview = '新对话';
          chat.message_count = 0;
          saveChatHistory();
        }
        ElMessage.success('已清空');
      } catch (e) {
        if (e !== 'cancel') {
          console.error('清空失败:', e);
        }
      }
    };

    // ==================== 消息发送 ====================
    const handleEnter = e => {
      if (e.shiftKey) {
        // Shift+Enter 换行
        return;
      }
      e.preventDefault();
      sendMessage();
    };
    const handleInput = () => {
      // 可以在这里实现输入提示等功能
    };
    const sendQuickMessage = prompt => {
      inputMessage.value = prompt;
      sendMessage();
    };
    const sendMessage = async (content = null) => {
      const message = content || inputMessage.value.trim();
      if (!message || isTyping.value) return;

      // 清空输入框
      if (!content) {
        inputMessage.value = '';
      }

      // 添加用户消息
      messages.value.push({
        role: 'user',
        content: message,
        timestamp: new Date().toISOString(),
        references: []
      });
      scrollToBottom();
      isTyping.value = true;
      try {
        // 准备上传的文件内容
        const filesData = uploadedFiles.value.map(file => ({
          filename: file.name,
          content: file.content,
          content_type: file.content_type,
          size: file.size
        }));

        // 调用流式 API
        const response = await fetch('/api/chat/stream', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${localStorage.getItem('token')}`
          },
          body: JSON.stringify({
            message,
            chatId: currentChatId.value,
            papers: selectedPapers.value.map(p => p.id),
            model: currentModel.value,
            temperature: settings.value.temperature,
            useRag: useRag.value,
            useWebSearch: useWebSearch.value,
            files: filesData.length > 0 ? filesData : undefined
          })
        });
        const reader = response.body.getReader();
        const decoder = new TextDecoder();
        let fullContent = '';

        // 添加 AI 消息占位
        messages.value.push({
          role: 'assistant',
          content: '',
          timestamp: new Date().toISOString(),
          references: []
        });

        // eslint-disable-next-line no-constant-condition
        while (true) {
          const {
            done,
            value
          } = await reader.read();
          if (done) break;
          const chunk = decoder.decode(value);
          const lines = chunk.split('\n\n');
          for (const line of lines) {
            if (line.startsWith('data: ')) {
              try {
                const data = JSON.parse(line.slice(6));
                if (data.done) {
                  isTyping.value = false;
                } else if (data.content) {
                  fullContent += data.content;
                  messages.value[messages.value.length - 1].content = fullContent;
                  scrollToBottom();
                }
              } catch (e) {
                console.error('解析 SSE 数据失败:', e);
              }
            }
          }
        }

        // 更新会话预览
        const chat = chatHistory.value.find(c => c.chat_id === currentChatId.value);
        if (chat) {
          chat.preview = message.slice(0, 30) + (message.length > 30 ? '...' : '');
          chat.message_count = messages.value.length;
          saveChatHistory();
        }
      } catch (error) {
        console.error('发送消息失败:', error);
        ElMessage.error('发送失败，请重试');
        isTyping.value = false;
      }
    };

    // ==================== 论文选择 ====================
    const loadPapers = async () => {
      try {
        const response = await api.getPapersList({
          limit: 1000
        });
        if (response.success) {
          papers.value = response.data.items || [];
          console.log(`[DEBUG] 加载了 ${papers.value.length} 篇论文`);
        } else {
          console.error('[ERROR] 加载论文失败:', response.error);
          papers.value = [];
        }
      } catch (error) {
        console.error('[ERROR] 加载论文失败:', error);
        papers.value = [];
      }
    };
    const handlePaperSelectionChange = selection => {
      tempSelectedPapers.value = selection;
    };
    const confirmPaperSelection = () => {
      selectedPapers.value = [...tempSelectedPapers.value];
      showPaperSelector.value = false;

      // 将关联论文保存到当前会话上下文
      const chat = chatHistory.value.find(c => c.chat_id === currentChatId.value);
      if (chat) {
        chat.connected_papers = selectedPapers.value.map(p => p.id);
        saveChatHistory();
      }
      ElMessage.success(`已关联 ${selectedPapers.value.length} 篇论文，RAG将优先检索这些论文`);
    };
    const viewPaper = paperId => {
      window.open(`#/papers/${paperId}`, '_blank');
    };

    // ==================== 设置 ====================
    const saveSettings = () => {
      localStorage.setItem('kimi_chat_settings', JSON.stringify(settings.value));
      showSettings.value = false;
      ElMessage.success('设置已保存');
    };
    const loadSettings = () => {
      try {
        const saved = localStorage.getItem('kimi_chat_settings');
        if (saved) {
          settings.value = {
            ...settings.value,
            ...JSON.parse(saved)
          };
        }
      } catch (e) {
        console.error('加载设置失败:', e);
      }
    };

    // ==================== 文件上传 ====================
    const handleUpload = () => {
      // 创建隐藏的文件输入
      const input = document.createElement('input');
      input.type = 'file';
      input.accept = '.txt,.pdf,.doc,.docx,.md,.py,.js,.json,.java,.c,.cpp,.html,.css';
      input.multiple = true;
      input.style.display = 'none';
      input.onchange = e => {
        handleFileSelect(e);
        // 清理创建的input元素
        document.body.removeChild(input);
      };
      document.body.appendChild(input);
      input.click();
    };
    const handleFileSelect = async event => {
      const files = event.target.files;
      if (!files || files.length === 0) return;
      isUploading.value = true;
      for (const file of files) {
        // 检查文件大小 (10MB)
        if (file.size > 10 * 1024 * 1024) {
          ElMessage.warning(`${file.name} 超过 10MB，已跳过`);
          continue;
        }
        try {
          const formData = new FormData();
          formData.append('file', file);
          ElMessage.info(`正在上传 ${file.name}...`);
          const response = await fetch('/api/chat/upload', {
            method: 'POST',
            headers: {
              'Authorization': `Bearer ${localStorage.getItem('token')}`
            },
            body: formData
          });
          const result = await response.json();
          if (result.success) {
            uploadedFiles.value.push({
              name: result.data.filename,
              hash: result.data.file_hash,
              content: result.data.content,
              content_type: result.data.content_type,
              size: result.data.size
            });
            ElMessage.success(`${file.name} 上传成功`);
          } else {
            ElMessage.error(`${file.name} 上传失败: ${result.error || '未知错误'}`);
          }
        } catch (error) {
          console.error('上传错误:', error);
          ElMessage.error(`${file.name} 上传失败: ${error.message || '网络错误'}`);
        }
      }
      isUploading.value = false;
    };
    const removeFile = index => {
      uploadedFiles.value.splice(index, 1);
    };

    // ==================== 生命周期 ====================
    onMounted(() => {
      loadChatHistory();
      loadPapers();
      loadSettings();

      // 如果没有会话，创建一个新的
      if (chatHistory.value.length === 0) {
        createNewChat();
      } else {
        // 切换到第一个会话
        switchChat(chatHistory.value[0].chat_id);
      }

      // 检查上传按钮可用性
      console.log('[DEBUG] KimiChat 已挂载，上传功能已就绪');
    });
    const __returned__ = {
      store,
      sidebarCollapsed,
      currentChatId,
      chatHistory,
      messages,
      inputMessage,
      isTyping,
      currentModel,
      messagesContainer,
      useRag,
      useWebSearch,
      selectedPapers,
      uploadedFiles,
      isUploading,
      showPaperSelector,
      paperSearch,
      papers,
      tempSelectedPapers,
      paperTable,
      showSettings,
      settings,
      showRenameDialog,
      renameValue,
      renameChatId,
      userAvatar,
      userInitial,
      currentChatTitle,
      canSend,
      inputRows,
      filteredPapers,
      quickCards,
      exampleQuestions,
      formatTime,
      renderMessage,
      scrollToBottom,
      loadChatHistory,
      saveChatHistory,
      createNewChat,
      switchChat,
      renameChat,
      confirmRename,
      deleteChat,
      clearCurrentChat,
      handleEnter,
      handleInput,
      sendQuickMessage,
      sendMessage,
      loadPapers,
      handlePaperSelectionChange,
      confirmPaperSelection,
      viewPaper,
      saveSettings,
      loadSettings,
      handleUpload,
      handleFileSelect,
      removeFile,
      ref,
      computed,
      onMounted,
      nextTick,
      watch,
      get useStore() {
        return useStore;
      },
      get ElMessage() {
        return ElMessage;
      },
      get ElMessageBox() {
        return ElMessageBox;
      },
      get Plus() {
        return Plus;
      },
      get Fold() {
        return Fold;
      },
      get Expand() {
        return Expand;
      },
      get ChatLineRound() {
        return ChatLineRound;
      },
      get MoreFilled() {
        return MoreFilled;
      },
      get Setting() {
        return Setting;
      },
      get Document() {
        return Document;
      },
      get Delete() {
        return Delete;
      },
      get ChatDotRound() {
        return ChatDotRound;
      },
      get Cpu() {
        return Cpu;
      },
      get Paperclip() {
        return Paperclip;
      },
      get Search() {
        return Search;
      },
      get Globe() {
        return Globe;
      },
      get Promotion() {
        return Promotion;
      },
      get Loading() {
        return Loading;
      },
      get Edit() {
        return Edit;
      },
      get api() {
        return api;
      },
      get marked() {
        return marked;
      },
      get DOMPurify() {
        return DOMPurify;
      },
      get hljs() {
        return hljs;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
