      // 确认是否要重新分析
      try {
        await ElMessageBox.confirm('重新分析将覆盖当前的分析结果，是否继续？', '确认操作', {
          confirmButtonText: '确定',
          cancelButtonText: '取消',
          type: 'warning'
        });

        // 清除历史数据标志
        isHistoricalData.value = false;
        analysisTime.value = '';

        // 清空当前结果
        result.value = null;

        // 开始新的分析
        await startAnalysis();
      } catch {
        // 用户取消操作
        ElMessage.info('已取消重新分析');
      }
    };
