    return {
      files,
      currentPaper,
      selectedTasks,
      analyzing,
      result,
      activeTab,
      isHistoricalData,
      analysisTime,
      categoryNames,
      keypointsDisplay,
      renderedSummary,
      selectPaper,
      startAnalysis,
      reanalyze,
      downloadResult,
      startEditGap,
      saveGap,
      cancelEditGap,
      generateCode,
      viewCode
    };
  }
};
